  const handlePasswordChange = ({target:{value}}) => setPassword(value);


  const handleFormSubmit = (event) =>{
    event.preventDefault();
    console.log(`Usuairo:${email} Password:${password}`);

  };


  return (
    <div className="App">
      <form onSubmit={handleFormSubmit}>
          <h2>Iniciar sesion</h2>
